import PubSub from 'pubsub-js';
export var TOPICS = {
  SHOW_TOAST: 'SHOW_TOAST',
  REMOVE_TOAST: 'REMOVE_TOAST'
};
export var toasts = {
  showToast: function showToast(toast) {
    PubSub.publish(TOPICS.SHOW_TOAST, toast);
  },
  removeToast: function removeToast(id) {
    PubSub.publish(TOPICS.REMOVE_TOAST, id);
  }
};
