import './App.css';
import API from './API';
import { useEffect, useState } from 'react';

import CallList from './Components/CallList';
import AppStyling from './AppStyling';

import { makeStyles } from '@material-ui/core/styles';

import Pusher from 'pusher-js';

let useStyles = makeStyles(AppStyling);
let pusher = new Pusher('d44e3d910d38a928e0be', {
  cluster: 'eu',
  authEndpoint: 'https://frontend-test-api.aircall.io/pusher/auth'
});

let channel = pusher.subscribe('private-aircall');

function App() {
  const [authToken, setAuthToken] = useState('');
  const [refreshToken, setRefreshToken] = useState('');
  const [page, setPages] = useState(0);

  const classes = useStyles();
  useEffect(() => {
    channel.bind('update-call', data => {
      console.log(data);
    });
    API.authenticate()
      .then(resp => resp.json())
      .then(data => {
        setAuthToken(data.access_token);
        setRefreshToken(data.refresh_token);
      });

    setInterval(() => {
      API.refreshAuthentication(refreshToken)
        .then(resp => resp.json())
        .then(data => () => {
          setAuthToken(data.access_token);
          setRefreshToken(data.refresh_token);
        });
    }, 600000);
  }, []);

  useEffect(() => {}, [authToken, page]);

  return (
    <div className="App">
      <CallList page={page} token={authToken} />
    </div>
  );
}

export default App;
