/// <reference types="react" />
export interface SliderProps {
    /**
     * The ID for when the slider is used, say, in a form.
     */
    id?: string;
    /**
     * Min value
     */
    min?: number;
    /**
     * Max value
     */
    max?: number;
    /**
     * Default value of the slider. Make sure it isn't smaller or larger than the min/max.
     */
    defaultValue?: number;
    /**
     * Value of the slider. Make sure it isn't smaller or larger than the min/max.
     */
    value?: number;
    /**
     * The onChange event of the slider.
     */
    onChange: (value: string, event: React.ChangeEvent<HTMLInputElement>) => void;
}
